import type { CanvasContext } from './dom';
import { GlobalRuntime } from './global-runtime';
import type { RenderingPlugin } from './services';
import { ClipSpaceNearZ, RendererConfig } from './types';

export interface RendererPlugin {
  name: string;
  context: CanvasContext;
  init: (runtime: GlobalRuntime) => void;
  destroy: (runtime: GlobalRuntime) => void;
}

export abstract class AbstractRendererPlugin<T = any>
  implements RendererPlugin
{
  context: CanvasContext & T;
  protected plugins = [];

  protected addRenderingPlugin(plugin: RenderingPlugin) {
    this.plugins.push(plugin);
    this.context.renderingPlugins.push(plugin);
  }

  protected removeAllRenderingPlugins() {
    this.plugins.forEach((plugin) => {
      const index = this.context.renderingPlugins.indexOf(plugin);
      if (index >= 0) {
        this.context.renderingPlugins.splice(index, 1);
      }
    });
  }

  abstract name: string;
  abstract init(runtime: GlobalRuntime): void;
  abstract destroy(runtime: GlobalRuntime): void;
}

export interface IRenderer {
  /**
   * The near/far clip planes correspond to a normalized device coordinate Z range of [0, 1],
   * which matches WebGPU/Vulkan/DirectX/Metal's clip volume, while [-1, 1] matches WebGL/OpenGL's clip volume.
   */
  clipSpaceNearZ: ClipSpaceNearZ;

  getConfig: () => RendererConfig;

  /**
   * register plugin at runtime
   */
  registerPlugin: (plugin: RendererPlugin) => void;

  /**
   * unregister plugin at runtime
   */
  unregisterPlugin: (plugin: RendererPlugin) => void;

  /**
   * get plugin by name
   */
  getPlugin: (name: string) => RendererPlugin;

  /**
   * return all registered plugins
   */
  getPlugins: () => RendererPlugin[];
}

export class AbstractRenderer implements IRenderer {
  clipSpaceNearZ = ClipSpaceNearZ.NEGATIVE_ONE;
  private plugins: RendererPlugin[] = [];
  private config: RendererConfig;

  constructor(config?: Partial<RendererConfig>) {
    this.config = {
      /**
       * only dirty object will cause re-render
       */
      enableDirtyCheck: true,
      enableCulling: false,
      /**
       * enable auto rendering by default
       */
      enableAutoRendering: true,
      /**
       * enable dirty rectangle rendering by default
       */
      enableDirtyRectangleRendering: true,
      enableDirtyRectangleRenderingDebug: false,
      enableSizeAttenuation: true,
      enableRenderingOptimization: false,
      ...config,
    };
  }

  registerPlugin(plugin: RendererPlugin) {
    const index = this.plugins.findIndex((p) => p === plugin);
    if (index === -1) {
      this.plugins.push(plugin);
    }
  }

  unregisterPlugin(plugin: RendererPlugin) {
    const index = this.plugins.findIndex((p) => p === plugin);
    if (index > -1) {
      this.plugins.splice(index, 1);
    }
  }

  getPlugins() {
    return this.plugins;
  }

  getPlugin(name: string) {
    return this.plugins.find((plugin) => plugin.name === name);
  }

  getConfig() {
    return this.config;
  }

  setConfig(config: Partial<RendererConfig>) {
    Object.assign(this.config, config);
  }
}
import type { mat4 } from 'gl-matrix';
import { vec3 } from 'gl-matrix';
import { Tuple3Number } from '../types';
import type { Plane } from './Plane';

const {
  add: addVec3,
  copy: copyVec3,
  max: maxVec3,
  min: minVec3,
  scale: scaleVec3,
  sub: subVec3,
} = vec3;

/**
 * Axis-Aligned Bounding Box
 * 为了便于后续 Frustum Culling，通过查找表定义 p-vertex 和 n-vertex
 * @see https://github.com/antvis/GWebGPUEngine/issues/3
 */
export class AABB {
  static isEmpty(aabb: AABB) {
    return (
      !aabb ||
      (aabb.halfExtents[0] === 0 &&
        aabb.halfExtents[1] === 0 &&
        aabb.halfExtents[2] === 0)
    );
  }

  center: Tuple3Number = [0, 0, 0];
  halfExtents: Tuple3Number = [0, 0, 0];
  min: Tuple3Number = [0, 0, 0];
  max: Tuple3Number = [0, 0, 0];

  update(center: Tuple3Number, halfExtents: Tuple3Number) {
    copyVec3(this.center, center);
    copyVec3(this.halfExtents, halfExtents);
    subVec3(this.min, this.center, this.halfExtents);
    addVec3(this.max, this.center, this.halfExtents);
  }

  setMinMax(min: Tuple3Number, max: Tuple3Number) {
    addVec3(this.center, max, min);
    scaleVec3(this.center, this.center, 0.5);
    subVec3(this.halfExtents, max, min);
    scaleVec3(this.halfExtents, this.halfExtents, 0.5);
    copyVec3(this.min, min);
    copyVec3(this.max, max);
  }

  getMin() {
    return this.min;
  }

  getMax() {
    return this.max;
  }

  add(aabb: AABB) {
    if (AABB.isEmpty(aabb)) {
      return;
    }
    if (AABB.isEmpty(this)) {
      this.setMinMax(aabb.getMin(), aabb.getMax());
      return;
    }

    const tc = this.center;
    const tcx = tc[0];
    const tcy = tc[1];
    const tcz = tc[2];
    const th = this.halfExtents;
    const thx = th[0];
    const thy = th[1];
    const thz = th[2];
    let tminx = tcx - thx;
    let tmaxx = tcx + thx;
    let tminy = tcy - thy;
    let tmaxy = tcy + thy;
    let tminz = tcz - thz;
    let tmaxz = tcz + thz;

    const oc = aabb.center;
    const ocx = oc[0];
    const ocy = oc[1];
    const ocz = oc[2];
    const oh = aabb.halfExtents;
    const ohx = oh[0];
    const ohy = oh[1];
    const ohz = oh[2];
    const ominx = ocx - ohx;
    const omaxx = ocx + ohx;
    const ominy = ocy - ohy;
    const omaxy = ocy + ohy;
    const ominz = ocz - ohz;
    const omaxz = ocz + ohz;

    if (ominx < tminx) {
      tminx = ominx;
    }
    if (omaxx > tmaxx) {
      tmaxx = omaxx;
    }
    if (ominy < tminy) {
      tminy = ominy;
    }
    if (omaxy > tmaxy) {
      tmaxy = omaxy;
    }
    if (ominz < tminz) {
      tminz = ominz;
    }
    if (omaxz > tmaxz) {
      tmaxz = omaxz;
    }

    tc[0] = (tminx + tmaxx) * 0.5;
    tc[1] = (tminy + tmaxy) * 0.5;
    tc[2] = (tminz + tmaxz) * 0.5;
    th[0] = (tmaxx - tminx) * 0.5;
    th[1] = (tmaxy - tminy) * 0.5;
    th[2] = (tmaxz - tminz) * 0.5;

    this.min[0] = tminx;
    this.min[1] = tminy;
    this.min[2] = tminz;
    this.max[0] = tmaxx;
    this.max[1] = tmaxy;
    this.max[2] = tmaxz;
  }

  setFromTransformedAABB(aabb: AABB, m: mat4) {
    const bc = this.center;
    const br = this.halfExtents;
    const ac = aabb.center;
    const ar = aabb.halfExtents;

    const mx0 = m[0];
    const mx1 = m[4];
    const mx2 = m[8];
    const my0 = m[1];
    const my1 = m[5];
    const my2 = m[9];
    const mz0 = m[2];
    const mz1 = m[6];
    const mz2 = m[10];

    const mx0a = Math.abs(mx0);
    const mx1a = Math.abs(mx1);
    const mx2a = Math.abs(mx2);
    const my0a = Math.abs(my0);
    const my1a = Math.abs(my1);
    const my2a = Math.abs(my2);
    const mz0a = Math.abs(mz0);
    const mz1a = Math.abs(mz1);
    const mz2a = Math.abs(mz2);

    bc[0] = m[12] + mx0 * ac[0] + mx1 * ac[1] + mx2 * ac[2];
    bc[1] = m[13] + my0 * ac[0] + my1 * ac[1] + my2 * ac[2];
    bc[2] = m[14] + mz0 * ac[0] + mz1 * ac[1] + mz2 * ac[2];

    // vec3.set(
    //   bc,
    //   m[12] + mx0 * ac[0] + mx1 * ac[1] + mx2 * ac[2],
    //   m[13] + my0 * ac[0] + my1 * ac[1] + my2 * ac[2],
    //   m[14] + mz0 * ac[0] + mz1 * ac[1] + mz2 * ac[2],
    // );

    br[0] = mx0a * ar[0] + mx1a * ar[1] + mx2a * ar[2];
    br[1] = my0a * ar[0] + my1a * ar[1] + my2a * ar[2];
    br[2] = mz0a * ar[0] + mz1a * ar[1] + mz2a * ar[2];

    // vec3.set(
    //   br,
    //   mx0a * ar[0] + mx1a * ar[1] + mx2a * ar[2],
    //   my0a * ar[0] + my1a * ar[1] + my2a * ar[2],
    //   mz0a * ar[0] + mz1a * ar[1] + mz2a * ar[2],
    // );

    // this.min = vec3.sub(this.min, bc, br);
    // this.max = vec3.add(this.max, bc, br);

    subVec3(this.min, bc, br);
    addVec3(this.max, bc, br);
  }

  intersects(aabb: AABB) {
    const aMax = this.getMax();
    const aMin = this.getMin();
    const bMax = aabb.getMax();
    const bMin = aabb.getMin();

    return (
      aMin[0] <= bMax[0] &&
      aMax[0] >= bMin[0] &&
      aMin[1] <= bMax[1] &&
      aMax[1] >= bMin[1] &&
      aMin[2] <= bMax[2] &&
      aMax[2] >= bMin[2]
    );
  }

  intersection(aabb: AABB): AABB | null {
    if (!this.intersects(aabb)) {
      return null;
    }

    const intersection = new AABB();
    const min = maxVec3(
      [0, 0, 0],
      this.getMin(),
      aabb.getMin(),
    ) as Tuple3Number;
    const max = minVec3(
      [0, 0, 0],
      this.getMax(),
      aabb.getMax(),
    ) as Tuple3Number;
    intersection.setMinMax(min, max);

    return intersection;
  }

  /**
   * get n-vertex
   * @param plane plane of CullingVolume
   */
  getNegativeFarPoint(plane: Plane): Tuple3Number {
    if (plane.pnVertexFlag === 0x111) {
      return copyVec3([0, 0, 0], this.min) as Tuple3Number;
      // return vec3.copy(vec3.create(), this.min);
    }
    if (plane.pnVertexFlag === 0x110) {
      return [this.min[0], this.min[1], this.max[2]];
      // return vec3.fromValues(this.min[0], this.min[1], this.max[2]);
    }
    if (plane.pnVertexFlag === 0x101) {
      return [this.min[0], this.max[1], this.min[2]];
      // return vec3.fromValues(this.min[0], this.max[1], this.min[2]);
    }
    if (plane.pnVertexFlag === 0x100) {
      return [this.min[0], this.max[1], this.max[2]];
      // return vec3.fromValues(this.min[0], this.max[1], this.max[2]);
    }
    if (plane.pnVertexFlag === 0x011) {
      return [this.max[0], this.min[1], this.min[2]];
      // return vec3.fromValues(this.max[0], this.min[1], this.min[2]);
    }
    if (plane.pnVertexFlag === 0x010) {
      return [this.max[0], this.min[1], this.max[2]];
      // return vec3.fromValues(this.max[0], this.min[1], this.max[2]);
    }
    if (plane.pnVertexFlag === 0x001) {
      return [this.max[0], this.max[1], this.min[2]];
      // return vec3.fromValues(this.max[0], this.max[1], this.min[2]);
    }
    return [this.max[0], this.max[1], this.max[2]];
    // return vec3.fromValues(this.max[0], this.max[1], this.max[2]);
  }

  /**
   * get p-vertex
   * @param plane plane of CullingVolume
   */
  getPositiveFarPoint(plane: Plane): Tuple3Number {
    if (plane.pnVertexFlag === 0x111) {
      return copyVec3([0, 0, 0], this.max) as Tuple3Number;
      // return vec3.copy(vec3.create(), this.max);
    }
    if (plane.pnVertexFlag === 0x110) {
      return [this.max[0], this.max[1], this.min[2]];
      // return vec3.fromValues(this.max[0], this.max[1], this.min[2]);
    }
    if (plane.pnVertexFlag === 0x101) {
      return [this.max[0], this.min[1], this.max[2]];
      // return vec3.fromValues(this.max[0], this.min[1], this.max[2]);
    }
    if (plane.pnVertexFlag === 0x100) {
      return [this.max[0], this.min[1], this.min[2]];
      // return vec3.fromValues(this.max[0], this.min[1], this.min[2]);
    }
    if (plane.pnVertexFlag === 0x011) {
      return [this.min[0], this.max[1], this.max[2]];
      // return vec3.fromValues(this.min[0], this.max[1], this.max[2]);
    }
    if (plane.pnVertexFlag === 0x010) {
      return [this.min[0], this.max[1], this.min[2]];
      // return vec3.fromValues(this.min[0], this.max[1], this.min[2]);
    }
    if (plane.pnVertexFlag === 0x001) {
      return [this.min[0], this.min[1], this.max[2]];
      // return vec3.fromValues(this.min[0], this.min[1], this.max[2]);
    }
    return [this.min[0], this.min[1], this.min[2]];
    // return vec3.fromValues(this.min[0], this.min[1], this.min[2]);
  }
}
import { vec3 } from 'gl-matrix';

export class Plane {
  public distance: number;
  public normal: vec3;

  /**
   * lookup table for p-vertex & n-vertex when doing frustum culling
   */
  public pnVertexFlag: number;

  constructor(distance?: number, normal?: vec3) {
    this.distance = distance || 0;
    this.normal = normal || vec3.fromValues(0, 1, 0);
    this.updatePNVertexFlag();
  }

  public updatePNVertexFlag() {
    this.pnVertexFlag =
      (Number(this.normal[0] >= 0) << 8) +
      (Number(this.normal[1] >= 0) << 4) +
      Number(this.normal[2] >= 0);
  }

  public distanceToPoint(point: vec3) {
    return vec3.dot(point, this.normal) - this.distance;
  }

  public normalize() {
    const invLen = 1 / vec3.len(this.normal);
    vec3.scale(this.normal, this.normal, invLen);
    this.distance *= invLen;
  }

  public intersectsLine(start: vec3, end: vec3, point?: vec3) {
    const d0 = this.distanceToPoint(start);
    const d1 = this.distanceToPoint(end);
    const t = d0 / (d0 - d1);
    const intersects = t >= 0 && t <= 1;
    if (intersects && point) {
      vec3.lerp(point, start, end, t);
    }
    return intersects;
  }
}
import type { mat4 } from 'gl-matrix';
import { vec3 } from 'gl-matrix';
import { Plane } from './Plane';

export enum Mask {
  OUTSIDE = 0xffffffff,
  INSIDE = 0x00000000,
  INDETERMINATE = 0x7fffffff,
}

export class Frustum {
  public planes: Plane[] = [];

  constructor(planes?: Plane[]) {
    if (planes) {
      this.planes = planes;
    } else {
      for (let i = 0; i < 6; i++) {
        this.planes.push(new Plane());
      }
    }
  }

  /**
   * extract 6 planes from projectionMatrix
   * @see http://www8.cs.umu.se/kurser/5DV051/HT12/lab/plane_extraction.pdf
   */
  public extractFromVPMatrix(projectionMatrix: mat4) {
    // @ts-ignore
    const [
      m0,
      m1,
      m2,
      m3,
      m4,
      m5,
      m6,
      m7,
      m8,
      m9,
      m10,
      m11,
      m12,
      m13,
      m14,
      m15,
    ] = projectionMatrix;

    // right
    vec3.set(this.planes[0].normal, m3 - m0, m7 - m4, m11 - m8);
    this.planes[0].distance = m15 - m12;

    // left
    vec3.set(this.planes[1].normal, m3 + m0, m7 + m4, m11 + m8);
    this.planes[1].distance = m15 + m12;

    // bottom
    vec3.set(this.planes[2].normal, m3 + m1, m7 + m5, m11 + m9);
    this.planes[2].distance = m15 + m13;

    // top
    vec3.set(this.planes[3].normal, m3 - m1, m7 - m5, m11 - m9);
    this.planes[3].distance = m15 - m13;

    // far
    vec3.set(this.planes[4].normal, m3 - m2, m7 - m6, m11 - m10);
    this.planes[4].distance = m15 - m14;

    // near
    vec3.set(this.planes[5].normal, m3 + m2, m7 + m6, m11 + m10);
    this.planes[5].distance = m15 + m14;

    this.planes.forEach((plane) => {
      plane.normalize();
      plane.updatePNVertexFlag();
    });
  }
}
