export enum CameraTrackingMode {
  DEFAULT,
  ROTATIONAL,
  TRANSLATIONAL,
  CINEMATIC,
}

export enum CameraProjectionMode {
  ORTHOGRAPHIC,
  PERSPECTIVE,
}

export const CameraEvent = {
  UPDATED: 'updated',
