// eg. { t: 'scale', d: [CSSUnitValue(1), CSSUnitValue(2)] }
export interface ParsedTransform {
  t: string;
  d: CSSUnitValue[];
}

type PatternElement = string | number | null | CSSUnitValue;
type CastFunction =
