export class CSSPropertyAngle
  implements Partial<CSSProperty<CSSUnitValue, number>>
{
  mixer = mergeNumbers;

  calculator(
    name: string,
    oldParsed: CSSUnitValue,
    parsed: CSSUnitValue,
