      const minY = Math.min(...points.map((point) => point[1]));
      const maxY = Math.max(...points.map((point) => point[1]));

      const width = maxX - minX;
      const height = maxY - minY;
      const hwidth = width / 2;
      const hheight = height / 2;

      return {
        cx: minX + hwidth,
        cy: minY + hheight,
        hwidth,
        hheight,
