    const clip = vec3.set(this.tmpVec3, canvasP.x, canvasP.y, 0);
    vec3.transformMat4(this.tmpVec3, this.tmpVec3, vpMatrix);

    // Clip -> NDC -> Viewport, flip Y
    const { width, height } = this.context.config;
    return new Point(
      ((clip[0] + 1) / 2) * width,
      (1 - (clip[1] + 1) / 2) * height,
