    // outside canvas' viewport
    if (
      viewportX < 0 ||
      viewportY < 0 ||
      viewportX > width ||
      viewportY > height
    ) {
      return null;
    }

    const { x: clientX, y: clientY } = this.defaultView.viewport2Client({
