    });
    const { width, height } = this.defaultView.getConfig();
    // outside canvas' viewport
    if (
      viewportX < 0 ||
      viewportY < 0 ||
      viewportX > width ||
      viewportY > height
    ) {
      return [];
    }

    const { x: clientX, y: clientY } = this.defaultView.viewport2Client({
