import type { ICamera } from '../camera';
import type { DisplayObject } from '../display-objects/DisplayObject';
import type { Element } from '../dom';
import type { Plane } from '../shapes';
import { AABB, Mask } from '../shapes';
import { Shape } from '../types';
import type { CullingStrategyContribution } from './CullingPlugin';

// group is not a 2d shape
const shape2D = [
  Shape.CIRCLE,
  Shape.ELLIPSE,
  Shape.IMAGE,
  Shape.RECT,
  Shape.LINE,
  Shape.POLYLINE,
  Shape.POLYGON,
  Shape.TEXT,
  Shape.PATH,
  Shape.HTML,
];

export class FrustumCullingStrategy implements CullingStrategyContribution {
  isVisible(camera: ICamera, object: DisplayObject) {
    // return true;

    const { cullable } = object;
    if (!cullable.enable) {
      return true;
    }

    const renderBounds = object.getRenderBounds();

    if (AABB.isEmpty(renderBounds)) {
      return false;
    }

    // get VP matrix from camera
    const frustum = camera.getFrustum();

    const parentVisibilityPlaneMask = (object.parentNode as Element)?.cullable
      ?.visibilityPlaneMask;
    cullable.visibilityPlaneMask = this.computeVisibilityWithPlaneMask(
      object,
      renderBounds,
      parentVisibilityPlaneMask || Mask.INDETERMINATE,
      frustum.planes,
    );

    cullable.visible = cullable.visibilityPlaneMask !== Mask.OUTSIDE;

    return cullable.visible;
  }

  /**
   *
   * @see「Optimized View Frustum Culling Algorithms for Bounding Boxes」
   * @see https://github.com/antvis/GWebGPUEngine/issues/3
   *
   * * 基础相交测试 the basic intersection test
   * * 标记 masking @see https://cesium.com/blog/2015/08/04/fast-hierarchical-culling/
   * * TODO: 平面一致性测试 the plane-coherency test
   * * TODO: 支持 mesh 指定自身的剔除策略，参考 Babylon.js @see https://doc.babylonjs.com/how_to/optimizing_your_scene#changing-mesh-culling-strategy
   *
   * @param aabb aabb
   * @param parentPlaneMask mask of parent
   * @param planes planes of frustum
   */
  private computeVisibilityWithPlaneMask(
    object: DisplayObject,
    aabb: AABB,
    parentPlaneMask: Mask,
    planes: Plane[],
  ) {
    if (parentPlaneMask === Mask.OUTSIDE || parentPlaneMask === Mask.INSIDE) {
      // 父节点完全位于视锥内或者外部，直接返回
      return parentPlaneMask;
    }

    // Start with MASK_INSIDE (all zeros) so that after the loop, the return value can be compared with MASK_INSIDE.
    // (Because if there are fewer than 31 planes, the upper bits wont be changed.)
    let mask = Mask.INSIDE;

    const isShape2D = shape2D.indexOf(object.nodeName as Shape) > -1;

    // Use viewport culling for 2D shapes
    // @see https://github.com/antvis/g/issues/914
    for (let k = 0, len = planes.length; k < len; ++k) {
      // For k greater than 31 (since 31 is the maximum number of INSIDE/INTERSECTING bits we can store), skip the optimization.
      const flag = 1 << k;

      if ((parentPlaneMask & flag) === 0) {
        // 父节点处于当前面内部，可以跳过
        continue;
      }

      // skip near & far planes when testing 2D shapes
      if (isShape2D && (k === 4 || k === 5)) {
        continue;
      }

      // p-vertex n-vertex <-|plane p-vertex n-vertex
      // 使用 p-vertex 和 n-vertex 加速，避免进行平面和 aabb 全部顶点的相交检测
      const { normal, distance } = planes[k];
      if (
        vec3.dot(normal, aabb.getPositiveFarPoint(planes[k])) + distance <
        0
      ) {
        return Mask.OUTSIDE;
      }
      if (
        vec3.dot(normal, aabb.getNegativeFarPoint(planes[k])) + distance <
        0
      ) {
        // 和当前面相交，对应位置为1，继续检测下一个面
        mask |= flag;
      }
    }

    return mask;
  }
}
import type RBush from 'rbush';
import { runtime } from '../global-runtime';
import type { RBushNodeAABB } from '../components';
import { DisplayObject } from '../display-objects';
import type { FederatedEvent } from '../dom';
import { ElementEvent } from '../dom';
import type { RenderingPlugin, RenderingPluginContext } from '../services';
import { raf } from '../utils';
import { AABB } from '../shapes';

export class PrepareRendererPlugin implements RenderingPlugin {
  static tag = 'Prepare';
  private rBush: RBush<RBushNodeAABB>;

  private syncTasks = new Map<DisplayObject, boolean>();

  private isFirstTimeRendering = true;
  private syncing = false;

  isFirstTimeRenderingFinished = false;

  apply(context: RenderingPluginContext) {
    const { renderingService, renderingContext, rBushRoot } = context;
    const canvas = renderingContext.root.ownerDocument.defaultView;

    this.rBush = rBushRoot;

    const handleAttributeChanged = (e: FederatedEvent) => {
      const object = e.target as DisplayObject;
      object.renderable.dirty = true;
      renderingService.dirtify();
    };

    const handleBoundsChanged = (e: FederatedEvent) => {
      this.syncTasks.set(e.target as DisplayObject, e.detail.affectChildren);

      renderingService.dirtify();
    };

    const handleMounted = (e: FederatedEvent) => {
      const object = e.target as DisplayObject;

      if (runtime.enableSizeAttenuation) {
        runtime.styleValueRegistry.updateSizeAttenuation(
          object,
          canvas.getCamera().getZoom(),
        );
      }
    };

    const handleUnmounted = (e: FederatedEvent) => {
      const object = e.target as DisplayObject;
      const { rBushNode } = object;
      if (rBushNode.aabb) {
        this.rBush.remove(rBushNode.aabb);
      }

      this.syncTasks.delete(object);

      runtime.sceneGraphService.dirtifyToRoot(object);
      renderingService.dirtify();
    };

    renderingService.hooks.init.tap(PrepareRendererPlugin.tag, () => {
      canvas.addEventListener(ElementEvent.MOUNTED, handleMounted);
      canvas.addEventListener(ElementEvent.UNMOUNTED, handleUnmounted);
      canvas.addEventListener(
        ElementEvent.ATTR_MODIFIED,
        handleAttributeChanged,
      );
      canvas.addEventListener(ElementEvent.BOUNDS_CHANGED, handleBoundsChanged);
    });

    renderingService.hooks.destroy.tap(PrepareRendererPlugin.tag, () => {
      canvas.removeEventListener(ElementEvent.MOUNTED, handleMounted);
      canvas.removeEventListener(ElementEvent.UNMOUNTED, handleUnmounted);
      canvas.removeEventListener(
        ElementEvent.ATTR_MODIFIED,
        handleAttributeChanged,
      );
      canvas.removeEventListener(
        ElementEvent.BOUNDS_CHANGED,
        handleBoundsChanged,
      );
      this.syncTasks.clear();
    });

    const ric =
      runtime.globalThis.requestIdleCallback ?? raf.bind(runtime.globalThis);
    renderingService.hooks.endFrame.tap(PrepareRendererPlugin.tag, () => {
      if (this.isFirstTimeRendering) {
        this.isFirstTimeRendering = false;
        this.syncing = true;
        ric(() => {
          this.syncRTree(true);
          this.isFirstTimeRenderingFinished = true;
        });
      } else {
        this.syncRTree();
      }
    });
  }

  private syncNode(node: DisplayObject, force = false) {
    if (!node.isConnected) return;

    const rBushNode = node.rBushNode;

    // clear dirty node
    if (rBushNode.aabb) this.rBush.remove(rBushNode.aabb);

    const renderBounds = node.getRenderBounds();
    if (renderBounds) {
      const renderable = node.renderable;

      if (force) {
        if (!renderable.dirtyRenderBounds) {
          renderable.dirtyRenderBounds = new AABB();
        }
        // save last dirty aabb
        renderable.dirtyRenderBounds.update(
          renderBounds.center,
          renderBounds.halfExtents,
        );
      }

      const [minX, minY] = renderBounds.getMin();
      const [maxX, maxY] = renderBounds.getMax();

      if (!rBushNode.aabb) {
        rBushNode.aabb = {} as RBushNodeAABB;
      }
      rBushNode.aabb.displayObject = node;
      rBushNode.aabb.minX = minX;
      rBushNode.aabb.minY = minY;
      rBushNode.aabb.maxX = maxX;
      rBushNode.aabb.maxY = maxY;
    }

    if (rBushNode.aabb) {
      // TODO: NaN occurs when width/height of Rect is 0
      if (
        !isNaN(rBushNode.aabb.maxX) &&
        !isNaN(rBushNode.aabb.maxX) &&
        !isNaN(rBushNode.aabb.minX) &&
        !isNaN(rBushNode.aabb.minY)
      ) {
        return rBushNode.aabb;
      }
    }
  }

  private syncRTree(force = false) {
    if (!force && (this.syncing || this.syncTasks.size === 0)) {
      return;
    }

    this.syncing = true;

    // bounds changed, need re-inserting its children
    const bulk: RBushNodeAABB[] = [];
    const synced = new Set<DisplayObject>();

    const sync = (node: DisplayObject) => {
      if (!synced.has(node) && node.renderable) {
        const aabb = this.syncNode(node, force);
        if (aabb) {
          bulk.push(aabb);
          synced.add(node);
        }
      }
    };

    this.syncTasks.forEach((affectChildren, node) => {
      if (affectChildren) {
        node.forEach(sync);
      }

      let parent = node;
      while (parent) {
        sync(parent);
        parent = parent.parentElement as DisplayObject;
      }
    });

    // use bulk inserting, which is ~2-3 times faster
    // @see https://github.com/mourner/rbush#bulk-inserting-data
    this.rBush.load(bulk);

    bulk.length = 0;
    this.syncing = false;
  }
}
