import React, { useState } from 'react';
import {
    MenuFoldOutlined,
    MenuUnfoldOutlined,
    UserOutlined,
    DashboardOutlined,
    CarOutlined,
    ShoppingCartOutlined,
    FileDoneOutlined,
    AppstoreOutlined
} from '@ant-design/icons';
import { Layout, Menu, Button, theme, Row, Col, Card, Table, Tag } from 'antd';
import Users from '../pages/users';
import Delivery from './dilevery/listDilevery';
import deliveryImage from '../assets/images/delivery.png';
import DeliveryPieChart from '../pages/charts';
import Vehicle from './vehicle/listVehicle';
import RunSheet from './runSheet/listRunSheet';
import TrackDelivery from './suiviCommande';
import Manifest from './dilevery/listManifest';
import { useQuery } from '@tanstack/react-query';
import { axiosInstance } from '../lib/axios';

const { Header, Sider, Content } = Layout;

// Composant pour afficher le tableau des livraisons
const DeliveryTable = ({ deliveries, selectedStatus }) => {
    const renderStatus = (status) => {
        let color = 'default';
        switch (status) {
            case 'EnAttente':
                color = 'orange';
                break;
            case 'EnDepot':
                color = 'blue';
                break;
            case 'EnCours':
                color = 'green';
                break;
            case 'RetourE':
                color = 'red';
                break;
            case 'RetourD':
                color = 'cyan';
                break;
            case 'Livré':
                color = 'purple';
                break;
            default:
                color = 'gray';
        }
        return <Tag color={color}>{status}</Tag>;
    };

    const columns = [
        {
            title: 'Code',
            dataIndex: 'code',
            key: 'code',
        },
        {
            title: 'Client',
            dataIndex: 'client_name',
            key: 'client_name',
        },
        {
            title: 'Adresse',
            dataIndex: 'delivery_address',
            key: 'delivery_address',
        },
        {
            title: 'Gouvernement',
            dataIndex: 'government',
            key: 'government',
        },
        {
            title: 'Téléphone',
            dataIndex: 'phone1',
            key: 'phone1',
        },
        {
            title: 'Rue',
            dataIndex: 'street',
            key: 'street',
        },
        {
            title: 'Status',
            dataIndex: 'status',
            key: 'status',
            render: (text) => renderStatus(text),
        },
    ];

    return (
        <div style={{ marginTop: 20 }}>
            <h3>Livraisons - {selectedStatus}</h3>
            <Table
                columns={columns}
                dataSource={deliveries.filter(d => d.status === selectedStatus)}
                rowKey="_id"
                pagination={{ pageSize: 5 }}
            />
        </div>
    );
};

